rule "Symantec Traffic Logs"
when
    to_bool(regex("\\(TCP||ICMP||UDP||8\\)",to_string($message.message)).matches)
then
    set_fields(
        grok(
            "%{HOSTNAME:symantec_server} SymantecServer: %{GREEDYDATA:local_hostname},Local Host IP: %{IP:local_addr},Local Port: %{NUMBER:local_port},Local Host MAC: %{SymantecMAC:local_mac},Remote Host IP: %{IP:remote_addr},Remote Host Name: (%{GREEDYDATA:remote_hostname})?,Remote Port: %{NUMBER:remote_port},Remote Host MAC: %{SymantecMAC:remote_mac},(%{DATA:protocol})?,%{WORD:direction},Begin: %{GREEDYDATA:start_time},End Time: %{GREEDYDATA:end_time},Occurrences: %{INT:repeat_count},Application: %{GREEDYDATA:application},Rule: %{GREEDYDATA:rule_name},Location: %{GREEDYDATA:event_location},User Name: %{GREEDYDATA:user_origin},Domain Name: %{GREEDYDATA:domain_origin},Action: %{WORD:action},SHA-256: (%{SHA256hash: sha256_hash})?,MD-5:( %{MD5hash:md5_hash})?",to_string($message.message),true
        )
    );
end

rule "Symantec Scan Results"
when
    to_bool(regex("Scan ID",to_string($message.message)).matches)
then
    set_fields(
        grok(
            "SymantecServer: (%{GREEDYDATA:symantec_server})?Scan ID: %{INT:scan_id},Begin: %{GREEDYDATA:start_time},End Time: %{GREEDYDATA:end_time},%{WORD:status},Duration \\(seconds\\): %{INT:duration},User1: %{WORD:user1},User2: (%{WORD:user2})?,%{GREEDYDATA:event_description},%{GREEDYDATA:result},Command: %{GREEDYDATA:command},Threats: %{INT:threats},Infected: %{INT:infected},Total files: %{INT:files_scanned},Omitted: %{INT:not_scanned},Computer: %{HOSTNAME:src_host},IP Address: %{IP:src_ip},Domain Name: %{GREEDYDATA:domain_origin},Group Name: %{GREEDYDATA:symantec_ou},Server Name: %{HOSTNAME:sepm_server}",to_string($message.message),true
        )
    );
end

rule "Symantec Client Logs"
when
    to_bool(regex("\\,(Smc||REP||SYLINK||Symantec AntiVirus||Symantec Endpoint Protection||LiveUpdate Manager||Network Intrusion Protection Sys||Memory Exploit Mitigation System||CVE)\\,",to_string($message.message)).matches)
then
    set_fields(
        grok(
            "SymantecServer: %{HOSTNAME:symantec_server},%{DATA:src_hostname},Category: %{NUMBER:UNWANTED},%{DATA:event_category},Event Description:%{GREEDYDATA:event_description},Event time: %{GREEDYDATA:end_time},Group Name: %{GREEDYDATA:symantec_ou}",to_string($message.message),true
        )
    );
end
